import React from 'react';
import { useLocation } from "react-router-dom";

import { Card, CardContent, Container, Box, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Header from '../Header/';

const Campeoes = () => {

  let location = useLocation();
  const campeoes = location.state;
  const posicao = campoes.map((campeao, posicao) => (campeao[posicao + 1]))
  console.log(posicao)

  const classes = useStyles();

  return (
    <Container>
      <Header
        titulo="Campeonato de Filmes"
        subtitulo="Resultado Final"
        descricao="Veja o resultado final do Campeonato de Filmes de forma simples e rÃ¡pida"
      />

      {campeoes.map(campeao =>
        <Card key={campeao.id} className={classes.root}>
          <CardContent>
            <Typography variant="h4" component="h2">{campeao.titulo}</Typography>
          </CardContent>
          <CardContent className={classes.campeao}>
            <Box>{campeao.findIndex()}</Box>
          </CardContent>
        </Card>
      )}

    </Container>
  )
}

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexDirection: "row-reverse",
    justifyContent: "flex-end",
    marginBottom: 20
  },
  campeao: {
    display: "flex",
    alignItems: "center",
    background: "#484848",
    color: "#FFF",
    fontWeight: "bold",
  }
}));

export default Campeoes;
