import axios from 'axios'

const api = axios.create({ baseURL: "https://localhost:5001/filme/api" })

const options = {
  headers: {
    accept: 'application/json',
  }
};

export async function rodarCampeonato(filmes) {
  try {
    const response = await api.post('/campeonato', filmes, options);
    const { data } = response;
    console.log('response', response)
    return data;
  }
  catch (err) {
    return err
  }
};

export async function obterFilmes() {
  try {
    const response = await api.get('/filmes');
    const { data } = response;
    data.sort((a, b) => (a.titulo > b.titulo) ? 1 : -1)
    return data;
  }
  catch (err) {
    return err
  }
};
